def lis(nums):

    n = len(nums)
    
    #F(i): longest increasing subsequence that can be formed given than it ends with element i
    mem = [1]*n
    
    #F(0) = 1
    
    #F(i) = max{max{1(F(j) < F(i))*F(j) for all 0 <= j < i}, 1}
    for i in range(n):
        prev_largest = None
        for j in range(0, i):
            if nums[i] > nums[j] and (not prev_largest or mem[j] > mem[prev_largest]):
                prev_largest = j
        if prev_largest is not None:
            mem[i] = mem[prev_largest] + 1
            
    #Ans is max(F(i))
    return max(mem)
    
print(lis([10,9,2,5,3,7,101,18]))
    
